import { createContext, useEffect, useState } from 'react';
import React,{ useContext } from 'react';

var username = "ceapa";
var password;
var token= 0;
var userRegister;
var passRegister;




function login (setErrLogin, errLogin,setSes, loggedIn, setLoggedIn, regMsg, setRegMsg){
    return(
        <div>
            <h1>Login</h1>
            <input type="text" placeholder="username" onChange={(e) => username = e.target.value}></input>
            <input type="password" placeholder="password" onChange={(e) => password = e.target.value}></input>
            <button onClick={async () =>{
                try{
                await fetch('http://127.0.0.1:5000/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({username: username, password: password}),
                }).then((response) => {
                    if (response.status !== 200){
                        console.log("FAILED TO LOGIN");
                        setLoggedIn(false);
                        setErrLogin(true);
                    }else{
                        if (response.status === 200){
                            response.json().then((data) => {
                                console.log(data);
                                console.log("LOGGED IN");
                                token = data.session_key;
                                console.log(token);
                                setLoggedIn(true);
                                setErrLogin(false);
                                setSes(true);
                                //save token to cache
                                localStorage.setItem('token', token);
                            });
                        }
                    }
                })}catch (error){
                    console.log("FAILED TO LOGIN");
                    setLoggedIn(false);
                    setErrLogin(true)
                }
            }}>
                Login
            </button>
            {errLogin ? <h1>Failed to login</h1> : <h1></h1>}
            <h1>Register</h1>
            <input type="text" placeholder="username" onChange={(e) => userRegister = e.target.value}></input>
            <input type="password" placeholder="password" onChange={(e) => passRegister = e.target.value}></input>
            <button onClick={async () => {
                try{
                await fetch('http://127.0.0.1:5000/register', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({username: userRegister, password: passRegister}),
                }).then((response) => {
                    if (response.status !== 201){
                        if (response.status === 409){
                            setRegMsg(<h1>Username already exists</h1>);
                            console.log("USERNAME ALREADY EXISTS");

                        }else{
                        console.log("FAILED TO REGISTER");
                        setRegMsg(<h1>Failed to register</h1>);}
                    }else{
                        console.log("REGISTERED");
                        setRegMsg(<h1>Registered</h1>);
                    }
                })}catch (error){
                    console.log("FAILED TO REGISTER");
                }
            }}>Register</button>
            {regMsg}
        </div>
    )
}

function validateToken(setSes, loggedIn, setLoggedIn){
    try{
        fetch ('http://127.0.0.1:5000/validate/' + token, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        }).then((response) => {
            if (response.status !== 200){
                console.log("FAILED TO VALIDATE TOKEN");
                setLoggedIn(false);
                setSes(false);

            }else{
                console.log("TOKEN VALIDATED");
                setLoggedIn(true);
                setSes(true);
            }
        });
    }catch (error){
        console.log("FAILED TO VALIDATE TOKEN");
        setLoggedIn(false);
        setSes(false);
    }
}

function editProfile(setEdit, edit){
    return(
        <div>
            <h1>Edit Profile</h1>
            <input type="text" placeholder="username" onChange={(e) => username = e.target.value}></input>
            <input type="password" placeholder="password" onChange={(e) => password = e.target.value}></input>
            <button onClick={setEdit(false)}>Cancel</button>
            <button onClick={async () => {
                try{
                await fetch('http://127.0.0.1:5000/edit/'+token, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({username: username, password: password}),
                }).then((response) => {
                    if (response.status !== 200){
                        console.log("FAILED TO EDIT PROFILE");
                    }else{
                        console.log("PROFILE EDITED");
                    }
                })}catch (error){
                    console.log("FAILED TO EDIT PROFILE");
                }}}>Edit</button>
        </div>
    )           
}

function exterior(children, setSes, loggedIn, setLoggedIn, setEdit, edit){    
    
    return(
        <div>
            <button onClick={() => {setLoggedIn(false); setSes(false);token = 0;
                                localStorage.setItem('token', token);}}>Logout</button>
            <button onClick={() => {setEdit(true);}}>Edit Profile</button>
            {children}
        </div>
    )
}
export function UserContext({children}){
    const [loggedIn, setLoggedIn] = useState(false);
    const [errLogin, setErrLogin] = useState(false);
    const [ses, setSes] = useState(false);
    const [regMsg, setRegMsg] = useState();
    const [edit, setEdit] = useState(false);
    //get token from cache
    token = localStorage.getItem('token');
    console.log(token);
    if (token !== null){
        validateToken(setSes, loggedIn, setLoggedIn);
    }
    return(
        <div>
            {loggedIn ? exterior(children, setSes, loggedIn, setLoggedIn, setEdit, edit) : login(setErrLogin, errLogin, setSes, loggedIn, setLoggedIn, regMsg, setRegMsg)} 
        </div>
    )
}
