import { createContext, useEffect, useState } from 'react';
import React,{ useContext } from 'react';

var username = "ceapa";
var password;
var token;







function login (setLoggedIn, setErrLogin, errLogin){
    return(
        <div>
            <h1>Login</h1>
            <input type="text" placeholder="username" onChange={(e) => username = e.target.value}></input>
            <input type="password" placeholder="password" onChange={(e) => password = e.target.value}></input>
            <button onClick={() => {
                fetch('http://127.0.0.1:5000/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({username: username, password: password}),
                }).then((response) => {
                    if (response.status !== 200){
                        console.log("FAILED TO LOGIN");
                        setLoggedIn(false); 
                        setErrLogin(true);
                    }else{
                        if (response.status === 200){
                            response.json().then((data) => {
                                console.log(data);
                                console.log("LOGGED IN");
                                token = data.token;
                                console.log(token);
                                setErrLogin(false);
                            });
                        }
                    }
                });
            }}>
                Login
            </button>
            {errLogin && <h1>Failed to login</h1>}
        </div>
    )
}
function register (){
    return(
        <div>
            <h1>Logout</h1>
        </div>
    )
}


function exterior(children, setLoggedIn ,loggedIn){

    return(
        <div>
            {loggedIn ? <h1>Logged in</h1> : <h1>Not logged in</h1>}
            Logged as {username}<button onClick={() => setLoggedIn(false)}>Logout</button>
            {children}
        </div>
    )
}
export function UserContext({children}){
    const [loggedIn, setLoggedIn] = useState(false);
    const [errLogin, setErrLogin] = useState(false);
    return(
        <div>
            {loggedIn ? exterior(children, setLoggedIn, loggedIn) : login(setLoggedIn,setLoggedIn,errLogin)} 
        </div>
    )
}
