import { createContext, useEffect, useState } from 'react';
import React,{ useContext } from 'react';

var username = "ceapa";
var password;
var token= 0;





function login (setErrLogin, errLogin,setSes, loggedIn, setLoggedIn){
    return(
        <div>
            <h1>Login</h1>
            <input type="text" placeholder="username" onChange={(e) => username = e.target.value}></input>
            <input type="password" placeholder="password" onChange={(e) => password = e.target.value}></input>
            <button onClick={async () =>{
                await fetch('http://127.0.0.1:5000/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({username: username, password: password}),
                }).then((response) => {
                    if (response.status !== 200){
                        console.log("FAILED TO LOGIN");
                        setLoggedIn(false);
                        setErrLogin(true);
                    }else{
                        if (response.status === 200){
                            response.json().then((data) => {
                                console.log(data);
                                console.log("LOGGED IN");
                                token = data.session_key;
                                console.log(token);
                                setLoggedIn(true);
                                setErrLogin(false);
                                setSes(true);
                                //save token to cache
                                localStorage.setItem('token', token);
                            });
                        }
                    }
                });
            }}>
                Login
            </button>

        </div>
    )
}
function register (){
    return(
        <div>
            <h1>Logout</h1>
        </div>
    )
}

function validateToken(setSes, loggedIn, setLoggedIn){

    fetch ('http://127.0.0.1:5000/validate/' + token, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    }).then((response) => {
        if (response.status !== 200){
            console.log("FAILED TO VALIDATE TOKEN");
            setLoggedIn(false);
            setSes(false);

        }else{
            console.log("TOKEN VALIDATED");
            setLoggedIn(true);
            setSes(true);
        }
    });
}

function exterior(children, setSes, loggedIn, setLoggedIn){    
    
    return(
        <div>
            <button onClick={() => {setLoggedIn(false); setSes(false)}}>Logout</button>
            {children}
        </div>
    )
}
export function UserContext({children}){
    const [loggedIn, setLoggedIn] = useState(false);
    const [errLogin, setErrLogin] = useState(false);
    const [ses, setSes] = useState(false);
    return(
        <div>
            {loggedIn ? exterior(children, setSes, loggedIn, setLoggedIn) : login(setErrLogin, errLogin, setSes, loggedIn, setLoggedIn)} 
        </div>
    )
}
