import { createContext, useEffect, useState } from 'react';
import React,{ useContext } from 'react';

var username = "ceapa";
var password;
var token;


var loggedIn = false;




function login (setErrLogin, errLogin){
    return(
        <div>
            <h1>Login</h1>
            <input type="text" placeholder="username" onChange={(e) => username = e.target.value}></input>
            <input type="password" placeholder="password" onChange={(e) => password = e.target.value}></input>
            <button onClick={async () =>{
                await fetch('http://127.0.0.1:5000/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({username: username, password: password}),
                }).then((response) => {
                    if (response.status !== 200){
                        console.log("FAILED TO LOGIN");
                        loggedIn = false;
                        setErrLogin(true);
                    }else{
                        if (response.status === 200){
                            response.json().then((data) => {
                                console.log(data);
                                console.log("LOGGED IN");
                                token = data.session_key;
                                console.log(token);
                                loggedIn = true;
                                setErrLogin(false);
                            });
                        }
                    }
                });
            }}>
                Login
            </button>

        </div>
    )
}
function register (){
    return(
        <div>
            <h1>Logout</h1>
        </div>
    )
}

function validateToken(){

    fetch ('http://127.0.0.1:5000/validate/' + token, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    }).then((response) => {
        if (response.status !== 200){
            console.log("FAILED TO VALIDATE TOKEN");
            loggedIn = false;
        }else{
            console.log("TOKEN VALIDATED");
        }
    });
    return (<div></div>)
}

function exterior(children){

    return(
        <div>
            {validateToken()}
            Logged as {username}<button onClick={() => loggedIn = false}>Logout</button>
            {children}
        </div>
    )
}
export function UserContext({children}){
    const [errLogin, setErrLogin] = useState(false);
    return(
        <div>
            {loggedIn ? exterior(children) : login(setErrLogin, errLogin)} 
        </div>
    )
}
